mapper.update:
UPDATE BOARD
SET COUNT = (SELECT COUNT FROM BOARD WHERE BOARD_NO = #{boardNo}) + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*) FROM BOARD B JOIN MEMBER M ON(B.MEMBER_ID = M.MEMBER_ID) JOIN NORMALNOTICE N ON(B.BOARD_NO = N.BOARD_NO) WHERE B.STATUS = 'Y' AND B.BOARD_TYPE = '일반공지'
AND B.WRITER = #{writer}
AND B.TITLE LIKE '%' || #{title} || '%'
AND N.WRITE_DEPT = #{writeDept}

SELECT * FROM BOARD B JOIN MEMBER M ON(B.MEMBER_ID = M.MEMBER_ID) JOIN NORMALNOTICE N ON(B.BOARD_NO = N.BOARD_NO) WHERE B.STATUS = 'Y' AND B.BOARD_TYPE = '일반공지'
AND B.WRITER = #{writer}
AND B.TITLE LIKE '%' || #{title} || '%'
AND N.WRITE_DEPT = #{writeDept}
ORDER BY B.BOARD_NO DESC

