mapper.select:
SELECT *
FROM MEMBER 
WHERE MEMBER_ID=#{memberId}
AND MEMBER_PWD = #{memberPwd}

mapper.update:
UPDATE MEMBER 
SET LOGIN_CHECK='1' 
WHERE MEMBER_ID=#{memberId}

UPDATE MEMBER 
SET LOGIN_CHECK='0' 
WHERE MEMBER_ID=#{memberId}

